mapper.select:
select 

from tcim_product
where  id = #{0}

SELECT 801 AS cateId, "理财师推荐产品" AS cateName, ( SELECT tc.config_value FROM tsys_config tc WHERE tc.config_type = 'product_extendsCate_config' AND tc.config_key = 'productCate_Logo_801' ) AS cateLogoInvestor, count(*) AS count, IFNULL(MIN(tp.flow_min_rate),7) AS flowMinRateStatistics, IFNULL(MAX(tp.flow_max_rate),12) AS flowMaxRateStatistics, ( SELECT tc.config_value FROM tsys_config tc WHERE tc.config_type = 'product_extendsCate_config' AND tc.config_key = 'productCate_Declare_801' ) AS cateDeclare FROM tcim_product_ref tpr, tcim_product tp, tcim_org_info toi, tcim_product_statistics tps WHERE toi.org_number = tp.org_number AND tp.product_id = tps.product_id AND toi. STATUS = 1
AND toi.org_gray_status = 0
AND tp.buy_total_money > tps.buyed_total_money AND tp. STATUS = 1 AND tp.audit_status = 1 AND tp.product_id = tpr.product_id AND tpr.sale_user_id = #{cfplannerUserId,jdbcType=VARCHAR}

select t.id AS id, t.product_name AS productName, t.product_type AS productType, t.sale_start_time AS saleStartTime, t.is_flow AS isFlow, t.flow_min_rate AS flowMinRate, t.flow_max_rate AS flowMaxRate, t.is_fixed_deadline AS isFixedDeadline, t.dead_line_min_value AS deadLineMinValue, t.dead_line_max_value AS deadLineMaxValue, t.dead_line_min_self_defined AS deadLineMinSelfDefined, t.dead_line_max_self_defined AS deadLineMaxSelfDefined, t.buy_total_money AS buyTotalMoney, t.show_index AS showIndex, t.status AS status, t.product_id AS productId, t.org_number AS orgNumber, t.fee_ratio AS feeRatio, t.audit_status AS auditStatus, t.audit_time AS auditTime, ( SELECT tpic.sort FROM tcim_product_info_cate tpic WHERE tpic.product_id = t.product_id and tpic.cate_id = 1 ) AS hotIndex, toi.name As orgName FROM tcim_product t, tcim_org_info toi WHERE toi.org_number = t.org_number and toi.status = 1
and t.product_name LIKE CONCAT('%','${query.productName}','%' )
and t.org_number =#{query.orgNumber}
and t.product_type =#{query.productType}
and t.status =#{query.status}
and t.audit_status =#{query.auditStatus}
flowMinRate ASC
flowMaxRate DESC
deadLineMinValue ASC
deadLineMaxValue DESC
${item.name} ${item.dir}
,t.create_time DESC
order by t.create_time DESC

SELECT tp.product_id AS productId, tp.product_name AS productName, tp.buy_total_money AS buyTotalMoney, tp.sale_start_time AS saleStartTime, tp.sale_end_time AS saleEndTime, tp.product_type AS productType, tp. STATUS AS STATUS, toi. NAME AS orgName, tps.buyed_total_money AS buyedTotalMoney, (tp.buy_total_money - tps.buyed_total_money) AS surplusMoney FROM tcim_product tp, tcim_product_statistics tps, tcim_org_info toi WHERE tp.product_id = tps.product_id AND toi.org_number = tp.org_number AND toi. STATUS = 1
and tp.product_name LIKE CONCAT('%','${query.productName}','%' )
and tp.org_number =#{query.orgNumber}
and tp.status =#{query.status}
and tp.product_type =#{query.productType}
${item.name} ${item.dir}
,tp.create_time DESC

SELECT tui.user_name AS investorUserName, tui.mobile AS investorUserMobile, tpir.invest_amt AS investAmt, tpir.start_time AS startTime FROM tcim_product_invest_record tpir, tcrm_user_info tui WHERE tpir.user_id = tui.user_id AND tpir.product_id = #{query.productId}
and (tui.user_name LIKE CONCAT('%','${query.productNaOrNumber}','%' ) or tui.mobile LIKE CONCAT('%','${query.productNaOrNumber}','%' ))
and tpir.start_time >= #{query.investStartTime}
and tpir.start_time <= #{query.investEndTime}
order by tpir.start_time desc

SELECT tp.product_id AS productId, tp.product_name AS productName, tp.product_type AS productType, tp.status AS STATUS, tp.is_fixed_deadline AS isFixedDeadline, tp.dead_line_min_value AS deadLineMinValue, tp.dead_line_max_value AS deadLineMaxValue, tp.dead_line_min_self_defined AS deadLineMinSelfDefined, tp.dead_line_max_self_defined AS deadLineMaxSelfDefined, tp.is_flow AS isFlow, tp.flow_min_rate AS flowMinRate, tp.flow_max_rate AS flowMaxRate, tp.audit_time AS auditPassTime, toi. NAME AS orgName, ( SELECT sum(tofg.product_amount) FROM tcim_org_fee_gather tofg WHERE tofg.product_id = tp.product_id ) AS saleroom, ( SELECT sum(tf.yearpur_amount) FROM tcim_feedetail tf WHERE tf.product_id = tp.product_id AND tf.fee_type = '1001' ) AS saleroomYear, ( SELECT sum(tofg.fee_amount) FROM tcim_org_fee_gather tofg WHERE tofg.product_id = tp.product_id ) AS saleCost, ( SELECT count(DISTINCT tofg.user_id) FROM tcim_org_fee_gather tofg WHERE tofg.product_id = tp.product_id ) AS investNumberOfPeople FROM tcim_product tp, tcim_org_info toi
toi.org_number = tp.org_number and toi.status = 1 and tp.audit_status = 1
and tp.product_name LIKE CONCAT('%','${query.productName}','%' )
and tp.org_number = #{query.orgNumber}
and tp.status =#{query.status}
and tp.audit_time >= #{query.auditStartTime}
and tp.audit_time <= #{query.auditEndTime}
flowMinRate ASC
flowMaxRate DESC
deadLineMinValue ASC
deadLineMaxValue DESC
${item.name} ${item.dir}
,tp.create_time DESC

SELECT tp.flow_max_rate AS flowMaxRate, tp.flow_min_rate AS flowMinRate, tp.is_flow AS isFlow, tp.product_id AS productId, tp.product_name AS productName, tp.fee_ratio AS feeRatio, tp.is_fixed_deadline AS isFixedDeadline, tp.dead_line_min_value AS deadLineMinValue, tp.dead_line_max_value AS deadLineMaxValue, tp.dead_line_min_self_defined AS deadLineMinSelfDefined, tp.dead_line_max_self_defined AS deadLineMaxSelfDefined FROM tcim_product tp WHERE 1=1
AND tp.product_name LIKE CONCAT('%','${proName}','%' )
AND tp.org_number=#{orgNumber}

mapper.update:
UPDATE tcim_product
	SET fee_ratio = #{feeRatio}
where
 org_number = #{orgNumber}
AND STATUS = 1

mapper.select:
SELECT
FROM tcim_product_ref tpr, tcim_product tp, tcim_product_statistics tps, tcim_org_info toi WHERE tp.product_id = tps.product_id AND toi.org_number = tp.org_number AND toi.status = 1
AND toi.org_gray_status = 0
AND tp.buy_total_money > tps.buyed_total_money AND tp.status = 1 AND tp.audit_status = 1 AND tp.product_id = tpr.product_id AND tpr.investor_user_id = #{userId,jdbcType=VARCHAR} AND tpr.sale_user_id = #{cfplannerUserId,jdbcType=VARCHAR}
AND toi.org_number = #{orgNumber,jdbcType=VARCHAR}
ORDER BY tpr.recommend_time DESC limit 1
